mapper.select:
SELECT * FROM t_cmm_post where (


        member_id in
        #{item}



        or community_id in

            #{item}



) and state = 1

group by id order by updated_at desc

SELECT * FROM t_cmm_post where (


        member_id in
        #{item}



        or community_id in

            #{item}



) and state = 1

    and post_id     <  #{post_id}


    and updated_at     <=  #{updated_at}

group by id order by updated_at desc LIMIT #{pageSize}

SELECT count(id) as ct FROM t_cmm_post where (


        member_id in
        #{item}



        or community_id in

            #{item}



) and state = 1

SELECT
member_id
FROM
( SELECT member_id, count( member_id ) AS ccnt FROM t_cmm_post

    state = 1

        AND member_id NOT IN

            #{item}



GROUP BY member_id
ORDER BY ccnt DESC
) AS cpstcnt
WHERE
  cpstcnt.ccnt >= #{ccnt} 

    LIMIT #{limitSize}

select  *  from  t_cmm_post where  id in
( select cpc.post_id
from t_cmm_post_circle cpc where cpc.circle_id = #{circleId} AND cpc.default_link = 1 )
