mapper.select:
select t1.id, t1.choose_date, t1.game_id, t2.android_status_desc, t2.name, t2.origin, t2.icon, t2.tags, t2.score, t2.can_fast, t2.android_state, CASE WHEN t3.circle_name is null THEN '0' ELSE '1' END circle_name from t_new_game t1 left join t_game t2 on t1.game_id=t2.id left join t_cmm_circle t3 on t1.game_id=t3.game_id
t1.choose_date >= #{startTime}
and t1.choose_date < #{endTime}
order by t1.choose_date desc,t1.sort desc LIMIT #{startOffset} ,#{pageSize}

SELECT t1.id, t1.choose_date, t1.game_id, t2.android_state, t2.android_status_desc, t2.id as game_id, t2.icon, t2.name, t2.origin as region, t2.tags from t_new_game t1 left join t_game t2 on t1.game_id=t2.id left join t_cmm_circle t3 on t1.game_id=t3.game_id
t1.choose_date < #{chooseDate}
order by t1.choose_date desc,t1.sort desc LIMIT #{startOffset} ,#{pageSize}
