mapper.select:
	   SELECT
			member_id
		FROM
				( SELECT member_id, count( member_id ) AS ecnt FROM t_game_evaluation
                
                    state = 0
                    
                      AND   member_id NOT IN
                        
                            #{item}
                        
                    
                
				 GROUP BY member_id
                 ORDER BY ecnt desc
              ) AS evalcnt
		WHERE
             evalcnt.ecnt >= #{ecnt} 
            
                LIMIT  #{limitSize}

	   SELECT
			member_id
		FROM
				( SELECT member_id, count( member_id ) AS ccnt FROM t_cmm_post
                    
                        state = 1
                        
                           AND  member_id NOT IN
                            
                                #{item}
                            
                        
                    
                     GROUP BY member_id
                     ORDER BY ccnt DESC
               ) AS cpstcnt
		WHERE
              cpstcnt.ccnt >= #{ccnt} 
            
                LIMIT  #{limitSize}

