select
m.id,m.mobile_phone_num   AS mobilePhoneNum
FROM  t_member m  where    m.id not in  (
SELECT member_id FROM t_member_coupon mc WHERE mc.member_type = 2 and mc.state = 1
and mc.coupon_id = #{couponId}
)
 
     and m.`level` >=  #{level}
 
 limit 1000
